send-one-way-request.set-body:
@{
            return new JObject(
                new JProperty("id", context.RequestId),
                new JProperty("pk", context.Variables["partition-key-prefix"] + "inbound"),
                new JProperty("timestamp", context.Timestamp),
                new JProperty("Ocp-Apim-Subscription-Key", context.Subscription.Key),
                new JProperty("direction", "inbound"),
                new JProperty("x-correlation-id", context.Variables["correlation-id"]),
                new JProperty("model", JObject.Parse(context.Request.Body.As<string>(preserveContent: true))["model"].ToString()),
                new JProperty("body", context.Request.Body.As<string>(preserveContent: true))
            ).ToString();
        }
