send-one-way-request.set-body:
@{
            var result = JObject.Parse(context.Response.Body.As<string>(preserveContent: true));

            return new JObject(
                new JProperty("id", context.Variables["app-id"] + "|" + context.RequestId + "|outbound"),
                new JProperty("pk", context.Variables["partition-key"]),
                new JProperty("request-id", context.RequestId),
                new JProperty("timestamp", context.Timestamp),
                new JProperty("Ocp-Apim-Subscription-Key", context.Subscription.Key),
                new JProperty("direction", "outbound"),
                new JProperty("x-correlation-id", context.Variables["correlation-id"]),
                new JProperty("appid", context.Variables["app-id"]),
                new JProperty("completion_tokens", result["usage"]["completion_tokens"]),
                new JProperty("prompt_tokens", result["usage"]["prompt_tokens"]),
                new JProperty("total_tokens", result["usage"]["total_tokens"]),
                new JProperty("model", result["model"]),
                new JProperty("backend-id", context.Variables["backend-id"]),
                new JProperty("body", result)
            ).ToString();
        }
